(180, 1, '2016-10-15 14:45:12', '2016-10-15 14:45:12', '<div class="col-sm-12">\r\n<div class="box">\r\n<h1 class="brand-name text-center">Booking &amp; availability</h1>\r\n<hr />\r\n</div>\r\n<div class="box">\r\n<h2>Prices and availability</h2>\r\n<p>\r\nTrevyr Barn is available all year round. Prices vary according to the season from £475 to £900 per week. We are also happy to take bookings for short breaks and long weekends. <a href="http://localhost:81/wordpress-trevrybarn/wp-content/uploads/2016/04/termsandconditions.pdf">Click here</a> to download details of prices and availability.</p>\r\n<p>Changeover day is usually Friday but we can be flexible outside peak times; call or email to check.</p>\r\n</div>\r\n<div class="box">\r\n<h2>Deposits</h2>\r\nA booking deposit of 25% will be required to secure your dates, with final payment six weeks prior to arrival.\r\n</div>\r\n<div class="box">\r\n<h2>Booking</h2>\r\nTo make a booking, please contact Louise and Alex. Our\r\ndetails are on the <a href="contact.html">contact page LINK TO FIX</a>.\r\n</div>\r\n<div class="box">\r\n<h2>Terms and Conditions</h2>\r\nClick <a title="terms and conditions" href="http://localhost:81/wordpress-trevrybarn/wp-content/uploads/2016/04/pricelist.pdf">here</a> for our booking terms and conditions.\r\n</div>\r\n</div>\r\n</div>', 'Booking and availability', '', 'inherit', 'closed', 'closed', '', '97-revision-v1', '', '', '2016-10-15 14:45:12', '2016-10-15 14:45:12', '', 97, 'http://localhost:81/wordpress-trevrybarn/97-revision-v1/', 0, 'revision', '', 0),
(161, 1, '2016-05-03 12:00:37', '2016-05-03 12:00:37', '<a href="http://localhost:81/wordpress-trevrybarn/wp-content/uploads/2016/04/pricelist.pdf">pricelist</a><div class="row">\r\n<div class="col-sm-12">\r\n<div class="box">\r\n<h1 class="brand-name text-center">Booking &amp; availability</h1>\r\n<hr />\r\n</div>\r\n<div class="box">\r\n<h2>Prices and availability</h2>\r\n<p>\r\nTrevyr Barn is available all year round. Prices vary according to the season from £475 to £900 per week. We are also happy to take bookings for short breaks and long weekends. <a href="http://localhost:81/wordpress-trevrybarn/wp-content/uploads/2016/04/termsandconditions.pdf">Click here</a> to download details of prices and availability.</p>\r\n<p>Changeover day is usually Friday but we can be flexible outside peak times; call or email to check.</p>\r\n</div>\r\n<div class="box">\r\n<h2>Deposits</h2>\r\nA booking deposit of 25% will be required to secure your dates, with final payment six weeks prior to arrival.\r\n</div>\r\n<div class="box">\r\n<h2>Booking</h2>\r\nTo make a booking, please contact Louise and Alex. Our\r\ndetails are on the <a href="contact.html">contact page LINK TO FIX</a>.\r\n</div>\r\n<div class="box">\r\n<h2>Terms and Conditions</h2>\r\nClick <a title="terms and conditions" href="http://localhost:81/wordpress-trevrybarn/wp-content/uploads/2016/04/pricelist.pdf">here</a> for our booking terms and conditions.\r\n</div>\r\n</div>\r\n</div>', 'Booking and availability', '', 'inherit', 'closed', 'closed', '', '97-revision-v1', '', '', '2016-05-03 12:00:37', '2016-05-03 12:00:37', '', 97, 'http://localhost:81/wordpress-trevrybarn/97-revision-v1/', 0, 'revision', '', 0),
