(400, 1, '2016-10-27 15:58:35', '2016-10-27 15:58:35', '<div class="row">\r\n    <div class="box">\r\n      <div class="col-sm-12">\r\n        <h1 class="brand-name text-center">Downstairs</h1>\r\n        <hr />\r\n        <p> The barn has been beautifully furnished and equipped to offer a high level of comfort and convenience. Large windows provide fabulous views across the surrounding countryside.</p>\r\n      </div>\r\n    </div>\r\n  </div>\r\n  <div class="row">\r\n    <div class="box">\r\n      <div class="col-sm-4">\r\n       <img class="img-responsive img-border img-left" src="[insert_php] bloginfo(''stylesheet_directory''); [/insert_php]/images/Trevyrbarn_080711_0047.jpg" alt="Kitchen">\r\n      </div>\r\n      <div class="col-sm-8">\r\n        <h2>Kitchen</h2>\r\n        <p>The kitchen is well equipped to allow you to enjoy turning beautiful local produce into fantastic\r\n          meals for friends and family. The large oak dining table seats six comfortably,\r\n          with the option to extend to eight.</p>\r\n      </div>\r\n    </div>\r\n  </div>\r\n  <div class="row">\r\n    <div class="box">\r\n      <div class="col-sm-4">\r\n        <img class="img-responsive img-border img-left" src="[insert_php] bloginfo(''stylesheet_directory''); [/insert_php]/images/Trevyrbarn_080711_0064.jpg" alt="Sitting Room">\r\n      </div>\r\n      <div class="col-sm-8">\r\n        <h2>Sitting room</h2>\r\n        <p>Large sitting room with two comfortable leather sofas and a cast iron\r\n          wood burner set in a traditional fireplace. Freeview TV, DVD and WIFI will keep you fully entertained, as will the selection of DVDs, books, maps and games.</p>\r\n      </div>\r\n    </div>\r\n  </div>\r\n  <div class="row">\r\n    <div class="box">\r\n      <div class="col-sm-12">\r\n        <h2>Utility room</h2>\r\n        <p>There is a separate utility room with washing machine, and a drying room with a\r\n          tumble dryer and a deep Belfast sink for rinsing off any muddy\r\n          kit.</p>\r\n      </div>\r\n    </div>\r\n  ', 'Downstairs', '', 'inherit', 'closed', 'closed', '', '32-revision-v1', '', '', '2016-10-27 15:58:35', '2016-10-27 15:58:35', '', 32, 'http://localhost:81/wordpress-trevrybarn/32-revision-v1/', 0, 'revision', '', 0);
