(446, 1, '2016-10-30 13:20:59', '2016-10-30 13:20:59', '<div class="row">\r\n  <div class="box">\r\n    <div class="col-sm-12">\r\n      <h1 class="brand-name text-center">Booking & availability</h1>\r\n      <hr />\r\n    </div>\r\n  </div>\r\n</div>\r\n<div class="row">\r\n  <div class="box">\r\n    <div class="col-sm-12">\r\n      <h2>Prices and availability</h2>\r\n      <p> Trevyr Barn is available all year round. Prices vary according to the\r\n        season from £475 to £950 per week. We are also happy to take\r\n        bookings for short breaks and long weekends. Click <a href="[insert_php] bloginfo(''stylesheet_directory''); [/insert_php]/docs/pricelist.pdf" title="pricelist" target="_blank">here</a> for details of prices and availability.</p>\r\n      <p>Changeover day is usually Friday but we can be flexible outside peak times; call or email to check.</p>\r\n    </div>\r\n  </div>\r\n</div>\r\n<div class="row">\r\n  <div class="box">\r\n    <div class="col-sm-12">\r\n      <h2>Deposits</h2>\r\n      <p>A booking deposit of 25% will be required to secure your dates, with final payment six weeks prior to arrival.</p>\r\n    </div>\r\n  </div>\r\n</div>\r\n<div class="row">\r\n  <div class="box">\r\n    <div class="col-sm-12">\r\n      <h2>Booking and contact</h2>\r\n      <p>To check availability and prices please contact Louise and Alex.</p>\r\n      <p>Email: <strong>louise@trevyrbarn.co.uk</strong></p>\r\n      <p>Phone: <a href="tel:07786 243355"> 07786 243355</a></p>\r\n      <p>Whether you email or leave a message, please state your preferred dates and any alternative dates that you may be considering so that we can come back to you with all the information that you require.</p>\r\n    </div>\r\n  </div>\r\n</div>\r\n<div class="row">\r\n  <div class="box">\r\n    <div class="col-sm-12">\r\n      <h2>Terms and Conditions</h2>\r\n      <p>Click <a href="[insert_php] bloginfo(''stylesheet_directory''); [/insert_php]/docs/termsandconditions.pdf" target="_blank" title="terms and conditions">here</a> for our booking terms and conditions.</p>\r\n    </div>\r\n  </div>\r\n</div>\r\n<div class="row">\r\n  <div class="box">\r\n    <div class="col-sm-12">\r\n      <h2>From our guest book</h2>\r\n      <img class="img-responsive img-border pull-right" src="[insert_php] bloginfo(''stylesheet_directory''); [/insert_php]/images/spring.jpg" alt="Spring at Trevyr Barn">\r\n      <blockquote>\r\n        <p>"...after just a couple of days it felt like home. We can''t wait to come back."</p>\r\n        <footer><cite>Pat & Barry, Macclesfield.</cite></footer>\r\n      </blockquote>\r\n      <blockquote>\r\n        <p>"Great barn, location, walking, cycling and pub. Perfect."</p>\r\n        <footer><cite>Anne & Paul, Surrey.</cite></footer>\r\n      </blockquote>\r\n      <blockquote>\r\n        <p>" If you ever want to sell this piece of heaven - Call us first!"</p>\r\n        <footer><cite>Sandra & Rory, NZ.</cite></footer>\r\n      </blockquote>\r\n    </div>\r\n  </div>\r\n', 'Booking & Contact', '', 'inherit', 'closed', 'closed', '', '97-revision-v1', '', '', '2016-10-30 13:20:59', '2016-10-30 13:20:59', '', 97, 'http://localhost:81/wordpress-trevrybarn/97-revision-v1/', 0, 'revision', '', 0),
